               <div className="buttongroup">
                    <button className="button" value='C' onClick={()=>this.clear()}>C</button>
                    <button className="button" value='+/-' onClick={(e)=>this.onButtonPress(e)}>+/-</button>
                    <button className="button"  value='%' onClick={(e)=>this.onButtonPress(e)}>%</button>
