//   }

  convertToIndianFormat = (number) => {
    number = Number(number)
    if (number) {
        number = number.toString();
        var lastThree = number.substring(number.length - 3);
        var otherNumbers = number.substring(0, number.length - 3);
        if (otherNumbers !== '')
            lastThree = ',' + lastThree;
        var res = otherNumbers.replace(/\B(?=(\d{2})+(?!\d))/g, ",") + lastThree;
        return res;
    }
    else {
        return number;
    }
}
    render() {
        return (
            <div className="container">
                <div className="calculator">
                <div className="operatevalues"> {this.state.equation}</div>
               <div className="resultscreens">{this.convertToIndianFormat(this.state.display)}</div>
               <div className="buttongroup">
                    <button className="button" value='C' onClick={()=>this.clear()}>C</button>
                    <button className="button" value='+/-' onClick={()=>this.operInput()}>+/-</button>
                    <button className="button"  value='%' onClick={()=>this.operInput()}>%</button>
                    <button className="button operators" value='/' onClick={()=>this.operInput()}>รท</button>
                    <button className="button" value='7' onClick={()=>this.numInput()}>7</button>
                    <button className="button" value='8' onClick={()=>this.numInput()}>8</button>
                    <button className="button"  value='9' onClick={()=>this.numInput()}>9</button>
                    <button className="button operators"  value='*' onClick={()=>this.operInput()}>ร</button>
                    <button className="button"  value='4' onClick={()=>this.numInput()}>4</button>
                    <button className="button" value='5' onClick={()=>this.numInput()}>5</button>
                    <button className="button" value='6' onClick={()=>this.numInput()}>6</button>
                    <button className="button operators" value='-'  onClick={()=>this.operInput()}>-</button>
                    <button className="button" value='1' onClick={()=>this.numInput()}>1</button>
                    <button className="button" value="2" onClick={()=>this.numInput()}>2</button>
                    <button className="button" value='3' onClick={()=>this.numInput()}>3</button>
                    <button className="button operators" value='+' onClick={()=>this.operInput()}>+</button>
                    <button className="button" value='0' onClick={()=>this.numInput()}>0</button>
                    <button className="button" value='.' onClick={()=>this.decInput()}>.</button>
                    <button className="button equalicon" value='=' onClick={()=>this.calculate()}>=</button>
                </div>
                </div>
            </div>
        )
    }
}
