import React, { Component } from 'react'
import './Calculator.css'

class Calculator extends Component {
  state = {
    result: 0,
        equation: ""
      }

onButtonPress = event => {
    let equation = this.state.equation;
    const pressedButton = event.target.innerHTML;
    // if (pressedButton === 'C') return this.clear();
   if ((pressedButton >= '0' && pressedButton <= '9') || pressedButton === '.') equation += pressedButton;
    else if (['+', '-', 'Ã—', '/', '%'].indexOf(pressedButton) !== -1) equation += ' ' + pressedButton + ' ';
    else if (pressedButton === '=') {
      try {
        const evalResult = eval(equation);
        const result = Number.isInteger(evalResult)? evalResult : evalResult.toFixed(2);
        this.setState({result});
      } catch (error) {
        // alert('Invalid Mathematical Equation');
      }
    }
    else {
      equation = equation.trim();
      equation = equation.substr(0, equation.length - 1);
    }

    this.setState({equation: equation});
  }
  clear=()=> {
    this.setState({equation: '', result: 0});
  }
    render() {
        return (
            <div className="container">
                <div className="calculator">
                <span className="operatevalues"> {this.state.equation}</span>
               <h3>{this.state.result}</h3>
                    <button className="button" onClick={()=>this.clear()}>C</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>+/-</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>%</button>
                    <button className="button operator" onClick={(e)=>this.onButtonPress(e)}>Ã·</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>7</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>8</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>9</button>
                    <button className="button operator" onClick={(e)=>this.onButtonPress(e)}>Ã—</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>4</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>5</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>6</button>
                    <button className="button operator" onClick={(e)=>this.onButtonPress(e)}>-</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>1</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>2</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>3</button>
                    <button className="button operator" onClick={(e)=>this.onButtonPress(e)}>+</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>0</button>
                    <button className="button" onClick={(e)=>this.onButtonPress(e)}>.</button>
                    <button className="button equalicon" onClick={(e)=>this.onButtonPress(e)}>=</button>
                </div>
            </div>

        )
    }
}
export default Calculator;
