import React, { Component } from 'react'
import './Calculator.css'

class Calculator extends Component {
  state = {
        display: "0",
        equation: ""
      }

      numInput=(e)=>{
          const {equation,display} =this.state
        if(equation.match(/[0-9\.]$/) && !equation.includes("=")){
          if(equation.match(/[+\-*\/]/) == null){
            let val = equation + e.currentTarget.value;
            this.setState({
              display: val,
              equation: val
            });
          } else {
            this.setState({
              display: display + e.currentTarget.value,
              equation: equation + e.currentTarget.value
            });
          }
        } else if(equation.match(/[+\-*\/]$/)){
          let val = equation + e.currentTarget.value;
          this.setState({
            display: e.currentTarget.value,
            equation: val
          });
        } else if(display === "0" && e.currentTarget.value !== "0" || equation.includes("=")) {
          this.setState({
            display: e.currentTarget.value,
            equation: e.currentTarget.value
          });
        }
      }



    render() {
        return (
            <div className="container">
                <div className="calculator">
                    <button className="button">C</button>
                    <button className="button">+/-</button>
                    <button className="button">%</button>
                    <button className="button">รท</button>
                    <button className="button">7</button>
                    <button className="button">8</button>
                    <button className="button">9</button>
                    <button className="button">x</button>
                    <button className="button">4</button>
                    <button className="button">5</button>
                    <button className="button">6</button>
                    <button className="button">-</button>
                    <button className="button">1</button>
                    <button className="button">2</button>
                    <button className="button">3</button>
                    <button className="button">+</button>
                    <button className="button">0</button>
                    <button className="button">.</button>
                    <button className="button equalicon">=</button>
                </div>
            </div>

        )
    }
}
export default Calculator;
