import React, { Component } from 'react'
import './Calculator.css'

class Calculator extends Component {
  state = {
        display: "0",
        equation: ""
      }

      numInput=(e)=>{
          const {equation,display} =this.state
        if(equation.match(/[0-9\.]$/) && !equation.includes("=")){
          if(equation.match(/[+\-*\/]/) == null){
            let val = equation + e.currentTarget.value;
            this.setState({
              display: val,
              equation: val
            });
          } else {
            this.setState({
              display: display + e.currentTarget.value,
              equation: equation + e.currentTarget.value
            });
          }
        } else if(equation.match(/[+\-*\/]$/)){
          let val = equation + e.currentTarget.value;
          this.setState({
            display: e.currentTarget.value,
            equation: val
          });
        } else if(display === "0" && e.currentTarget.value !== "0" || equation.includes("=")) {
          this.setState({
            display: e.currentTarget.value,
            equation: e.currentTarget.value
          });
        }
      }

      calculate=()=>{
        if(this.state.equation.includes("=")){
          let val = `${this.state.display} = ${this.state.display}`;
          this.setState({
            equation: val
          });
        } else if(this.state.equation != "" && this.state.equation.match(/[+\-*\/]/) != null && this.state.equation.match(/[+\-*\/]$/) == null) {
          let result = Number.isInteger(eval(this.state.equation)) ? eval(this.state.equation) : parseFloat(eval(this.state.equation).toFixed(5));
          let val = this.state.equation;
          val += ` = ${result}`;
          this.setState({
            display: result,
            equation: val
          });
        }
      }

    clearInput=()=>{
    this.setState({
      display: "0",
      equation: ""
    });
  }


    render() {
        return (
            <div className="container">
                <div className="calculator">
                <h1 equation={this.state.equation} display={this.state.display} ></h1>

                    <button className="button" onClick={()=>this.clearInput}>C</button>
                    <button className="button">+/-</button>
                    <button className="button">%</button>
                    <button className="button">÷</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>7</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>8</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>9</button>
                    <button className="button">x</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>4</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>5</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>6</button>
                    <button className="button" >-</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>1</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>2</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>3</button>
                    <button className="button">+</button>
                    <button className="button" onClick={(e)=>this.numInput(e)}>0</button>
                    <button className="button">.</button>
                    <button className="button equalicon" onClick={()=>this.calculate()}>=</button>
                </div>
            </div>

        )
    }
}
export default Calculator;
